package Com.evegent.corejava.Strings;

public class StringDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1="Hello";
		String str2="Hello";
		if(str1==str2) 
			System.out.println("True");
		else
			System.out.println("False");
		
		if(str1.equals(str2)) 
			System.out.println("True");
		else
			System.out.println("False");
		
	}

}
